"use client";

import { useEffect } from "react";
import { useRouter } from "next/navigation";
import { useAuthStore } from "@/lib/store/authStore";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { CreateFamilyForm } from "@/components/onboarding/CreateFamilyForm";
import { JoinFamilyForm } from "@/components/onboarding/JoinFamilyForm";
import { AuthLoadingScreen } from "@/lib/hooks/useRequireAuth";
import { LogOut } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useAuth } from "@/components/providers/AuthProvider";

export default function GetStartedPage() {
  const { user, profile, loading, activeFamilyId } = useAuthStore();
  const { signOut } = useAuth();
  const router = useRouter();

  useEffect(() => {
    if (!loading && !user) {
      router.push("/");
      return;
    }

    if (!loading && user && activeFamilyId) {
      router.push("/today");
    }
  }, [user, loading, activeFamilyId, router]);

  if (loading || !user) {
    return <AuthLoadingScreen />;
  }

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4 bg-secondary/30">
      <div className="w-full max-w-4xl grid md:grid-cols-2 gap-8">
        <div className="md:col-span-2 text-center space-y-2 mb-4">
          <h1 className="text-4xl font-bold tracking-tight text-primary">
            Welcome, {user.displayName?.split(" ")[0] || "Chef"}!
          </h1>
          <p className="text-muted-foreground text-lg">
            To retrieve your meal plans, we need to know properly which kitchen
            you belong to.
          </p>
        </div>

        <Card className="border-2 hover:border-primary/50 transition-colors">
          <CardHeader>
            <CardTitle>Start a New Kitchen</CardTitle>
            <CardDescription>
              Create a new family space and invite others to join you.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <CreateFamilyForm />
          </CardContent>
        </Card>

        <Card className="border-transparant hover:bg-card/50 transition-colors">
          <CardHeader>
            <CardTitle>Join an Existing Kitchen</CardTitle>
            <CardDescription>
              Have an invite code? Enter it here to sync up.
            </CardDescription>
          </CardHeader>
          <CardContent>
            <JoinFamilyForm />
          </CardContent>
        </Card>

        <div className="md:col-span-2 flex justify-center mt-8">
          <Button
            variant="ghost"
            onClick={() => signOut()}
            className="text-muted-foreground"
          >
            <LogOut className="mr-2 h-4 w-4" />
            Sign Out
          </Button>
        </div>
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import {
  format,
  startOfMonth,
  endOfMonth,
  eachDayOfInterval,
  startOfWeek,
  endOfWeek,
  isSameMonth,
  isSameDay,
} from "date-fns";
// import { AppShell } from "@/components/layout/AppShell"; // Removed

import { useAuth } from "@/components/providers/AuthProvider";
import { useRequireAuth, AuthLoadingScreen } from "@/lib/hooks/useRequireAuth";
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "@/lib/firebase/config";
import { DayPlan } from "@/types";
import { cn } from "@/lib/utils/cn";
import { useRouter } from "next/navigation";
import { Button } from "@/components/ui/button";
import { ChevronLeft, ChevronRight, Loader2 } from "lucide-react";

export default function CalendarPage() {
  const {
    user: authUser,
    loading: authLoading,
    activeFamilyId,
  } = useRequireAuth();
  const { user } = useAuth();
  const router = useRouter();
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [monthPlans, setMonthPlans] = useState<Record<string, DayPlan>>({});
  const [loading, setLoading] = useState(true);

  // Generate calendar grid
  const monthStart = startOfMonth(currentMonth);
  const monthEnd = endOfMonth(monthStart);
  const calendarStart = startOfWeek(monthStart);
  const calendarEnd = endOfWeek(monthEnd);

  const calendarDays = eachDayOfInterval({
    start: calendarStart,
    end: calendarEnd,
  });

  useEffect(() => {
    if (!user || !activeFamilyId) {
      setLoading(false);
      return;
    }

    const fetchMonth = async () => {
      setLoading(true);
      try {
        const startStr = format(calendarStart, "yyyy-MM-dd");
        const endStr = format(calendarEnd, "yyyy-MM-dd");

        const q = query(
          collection(db, "families", activeFamilyId, "dayPlans"),
          where("date", ">=", startStr),
          where("date", "<=", endStr),
        );

        const snapshot = await getDocs(q);
        const plans: Record<string, DayPlan> = {};
        snapshot.forEach((doc) => {
          const data = doc.data();
          const mealsMap = data.meals || {};
          const id = doc.id;

          plans[id] = {
            id: data.id || id,
            date: data.date || id,
            familyId: activeFamilyId,
            updatedAt: data.updatedAt || 0,
            mealCount: data.mealCount || 0,
            meals: {
              breakfast: mealsMap.breakfast || data["meals.breakfast"] || [],
              lunch: mealsMap.lunch || data["meals.lunch"] || [],
              snack: mealsMap.snack || data["meals.snack"] || [],
              dinner: mealsMap.dinner || data["meals.dinner"] || [],
            },
          };
        });
        setMonthPlans(plans);
      } catch (error) {
        console.error("Error fetching month:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchMonth();
  }, [user, currentMonth, activeFamilyId]);

  const previousMonth = () => {
    setCurrentMonth(
      (prev) => new Date(prev.getFullYear(), prev.getMonth() - 1, 1),
    );
  };

  const nextMonth = () => {
    setCurrentMonth(
      (prev) => new Date(prev.getFullYear(), prev.getMonth() + 1, 1),
    );
  };

  const navigateToDay = (dateStr: string) => {
    router.push(`/today?date=${dateStr}`);
  };

  if (authLoading || !authUser) {
    return <AuthLoadingScreen />;
  }

  if (loading && !Object.keys(monthPlans).length) {
    return (
      <div className="flex justify-center p-12">
        <Loader2 className="w-8 h-8 animate-spin text-soft-sage" />
      </div>
    );
  }

  return (
    <div className="mx-auto max-w-4xl h-full flex flex-col pb-8">
      <header className="mb-6 flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-text-dark dark:text-foreground">
            Calendar
          </h1>
          <p className="text-stone-500 dark:text-stone-400">
            {format(currentMonth, "MMMM yyyy")}
          </p>
        </div>
        <div className="flex gap-2 bg-white dark:bg-card p-1 rounded-xl border border-stone-100 dark:border-stone-800 shadow-sm">
          <Button
            variant="ghost"
            size="icon"
            onClick={previousMonth}
            className="h-8 w-8 hover:bg-stone-50 dark:hover:bg-stone-800"
          >
            <ChevronLeft className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            onClick={nextMonth}
            className="h-8 w-8 hover:bg-stone-50 dark:hover:bg-stone-800"
          >
            <ChevronRight className="h-4 w-4" />
          </Button>
        </div>
      </header>

      <div className="grid grid-cols-7 gap-px rounded-2xl bg-stone-100 dark:bg-stone-800 border border-stone-200 dark:border-stone-700 overflow-hidden flex-1 shadow-sm">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((day) => (
          <div
            key={day}
            className="bg-stone-50 dark:bg-stone-900 py-3 text-center text-[10px] font-bold text-stone-400 uppercase tracking-widest"
          >
            {day}
          </div>
        ))}

        {calendarDays.map((day) => {
          const dateStr = format(day, "yyyy-MM-dd");
          const plan = monthPlans[dateStr];
          const isCurrentMonth = isSameMonth(day, currentMonth);
          const isToday = isSameDay(day, new Date());

          const hasBreakfast = plan?.meals?.breakfast?.length ? true : false;
          const hasLunch = plan?.meals?.lunch?.length ? true : false;
          const hasDinner = plan?.meals?.dinner?.length ? true : false;
          const hasSnack = plan?.meals?.snack?.length ? true : false;

          return (
            <div
              key={dateStr}
              onClick={() => navigateToDay(dateStr)}
              className={cn(
                "relative bg-white dark:bg-card p-2 transition-all hover:z-10 hover:shadow-lg cursor-pointer flex flex-col min-h-[100px] gap-1",
                !isCurrentMonth &&
                  "bg-stone-50/30 dark:bg-stone-900/10 text-stone-300",
              )}
            >
              <time
                dateTime={dateStr}
                className={cn(
                  "flex h-7 w-7 items-center justify-center rounded-full text-sm font-medium transition-colors",
                  isToday
                    ? "bg-soft-sage text-white shadow-sm"
                    : isCurrentMonth
                      ? "text-stone-700 dark:text-stone-300"
                      : "text-stone-300 dark:text-stone-600",
                )}
              >
                {format(day, "d")}
              </time>

              <div className="mt-1 flex flex-col gap-1 overflow-hidden text-[10px]">
                {hasBreakfast &&
                  plan?.meals?.breakfast?.map((m) => (
                    <div
                      key={m.id}
                      className="truncate px-1.5 py-0.5 rounded-md bg-orange-50 text-orange-700 border border-orange-100 dark:bg-orange-900/20 dark:text-orange-300 dark:border-orange-900/30"
                    >
                      {m.name}
                    </div>
                  ))}
                {hasLunch &&
                  plan?.meals?.lunch?.map((m) => (
                    <div
                      key={m.id}
                      className="truncate px-1.5 py-0.5 rounded-md bg-green-50 text-green-700 border border-green-100 dark:bg-green-900/20 dark:text-green-300 dark:border-green-900/30"
                    >
                      {m.name}
                    </div>
                  ))}
                {hasSnack &&
                  plan?.meals?.snack?.map((m) => (
                    <div
                      key={m.id}
                      className="truncate px-1.5 py-0.5 rounded-md bg-purple-50 text-purple-700 border border-purple-100 dark:bg-purple-900/20 dark:text-purple-300 dark:border-purple-900/30"
                    >
                      {m.name}
                    </div>
                  ))}
                {hasDinner &&
                  plan?.meals?.dinner?.map((m) => (
                    <div
                      key={m.id}
                      className="truncate px-1.5 py-0.5 rounded-md bg-blue-50 text-blue-700 border border-blue-100 dark:bg-blue-900/20 dark:text-blue-300 dark:border-blue-900/30"
                    >
                      {m.name}
                    </div>
                  ))}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

"use client";

import { useGrocery } from "@/lib/hooks/useGrocery";
import { useState } from "react";
import {
  Plus,
  Trash2,
  ShoppingCart,
  RefreshCw,
  Check,
  Calendar,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { cn } from "@/lib/utils/cn";
import { Loader2 } from "lucide-react";
import {
  startOfDay,
  endOfDay,
  startOfWeek,
  endOfWeek,
  startOfMonth,
  endOfMonth,
  addDays,
} from "date-fns";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";

export default function GroceryPage() {
  const { items, loading, addItem, toggleItem, deleteItem, generateFromRange } =
    useGrocery();
  const [newItemName, setNewItemName] = useState("");
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [rangeType, setRangeType] = useState<"Today" | "Week" | "Month">(
    "Week",
  );

  const handleAddItem = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newItemName.trim()) return;
    addItem(newItemName.trim());
    setNewItemName("");
    setIsAddDialogOpen(false);
  };

  const handleGenerate = async () => {
    setIsGenerating(true);
    const now = new Date();
    let start = new Date();
    let end = new Date();

    if (rangeType === "Today") {
      start = startOfDay(now);
      end = endOfDay(now);
    } else if (rangeType === "Week") {
      start = startOfWeek(now, { weekStartsOn: 1 });
      end = endOfWeek(now, { weekStartsOn: 1 });
    } else if (rangeType === "Month") {
      // Simple month range: 1st to last day
      start = startOfMonth(now);
      end = endOfMonth(now);
    }

    await generateFromRange(start, end);
    setIsGenerating(false);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <Loader2 className="animate-spin text-soft-sage w-8 h-8" />
      </div>
    );
  }

  const pendingItems = items.filter((i) => !i.completed);
  const completedItems = items.filter((i) => i.completed);

  return (
    <div className="space-y-6 pb-24 md:pb-0">
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <h1 className="text-2xl font-bold text-text-dark dark:text-foreground flex items-center gap-2">
          <ShoppingCart className="w-6 h-6 text-soft-sage" />
          Grocery List
        </h1>

        <div className="flex items-center gap-2">
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="outline"
                size="sm"
                className="gap-2 min-w-[120px]"
              >
                <Calendar className="w-4 h-4 text-stone-500" />
                {rangeType}
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem onClick={() => setRangeType("Today")}>
                Today
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => setRangeType("Week")}>
                This Week
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => setRangeType("Month")}>
                This Month
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>

          <Button
            variant="outline"
            size="sm"
            onClick={handleGenerate}
            disabled={isGenerating}
            className="text-soft-sage border-soft-sage/30 hover:bg-soft-sage/10 whitespace-nowrap"
          >
            {isGenerating ? (
              <Loader2 className="w-4 h-4 animate-spin mr-2" />
            ) : (
              <RefreshCw className="w-4 h-4 mr-2" />
            )}
            Sync
          </Button>
        </div>
      </div>

      {/* Add Item Dialog */}
      <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add to Grocery List</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleAddItem} className="space-y-4 mt-4">
            <Input
              placeholder="Item name (e.g. Milk, Eggs...)"
              value={newItemName}
              onChange={(e) => setNewItemName(e.target.value)}
              autoFocus
            />
            <div className="flex justify-end gap-2">
              <Button
                type="button"
                variant="ghost"
                onClick={() => setIsAddDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={!newItemName.trim()}
                className="bg-soft-sage text-white"
              >
                Add Item
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {/* Floating Action Button */}
      <div className="fixed bottom-20 right-4 md:bottom-8 md:right-8 z-50">
        <Button
          size="icon"
          className="h-14 w-14 rounded-full shadow-lg bg-soft-sage hover:bg-soft-sage/90 text-white"
          onClick={() => setIsAddDialogOpen(true)}
        >
          <Plus className="h-6 w-6" />
        </Button>
      </div>

      {/* Lists */}
      <div className="space-y-6">
        {/* Pending Items */}
        <div className="space-y-2">
          {pendingItems.length === 0 && completedItems.length === 0 && (
            <div className="text-center py-10 text-stone-400 italic">
              Your list is empty. Add items or sync from your meal plan!
            </div>
          )}

          {pendingItems.map((item) => (
            <div
              key={item.id}
              className="flex items-center justify-between p-3 bg-white dark:bg-card rounded-xl border border-stone-100 dark:border-stone-800 shadow-sm transition-all hover:shadow-md group"
            >
              <div
                className="flex items-center gap-3 flex-1 cursor-pointer"
                onClick={() => toggleItem(item.id, true)}
              >
                <div className="w-5 h-5 rounded-full border-2 border-stone-300 dark:border-stone-600 flex-shrink-0 group-hover:border-soft-sage transition-colors" />
                <span className="text-text-dark dark:text-foreground font-medium">
                  {item.name}
                </span>
              </div>
              <button
                onClick={() => deleteItem(item.id)}
                className="text-stone-300 hover:text-red-400 p-2 transition-colors opacity-100 md:opacity-0 md:group-hover:opacity-100 focus:opacity-100"
              >
                <Trash2 className="w-4 h-4" />
              </button>
            </div>
          ))}
        </div>

        {/* Completed Items */}
        {completedItems.length > 0 && (
          <div className="space-y-2">
            <h2 className="text-sm font-bold text-stone-400 uppercase tracking-wider">
              Completed
            </h2>
            {completedItems.map((item) => (
              <div
                key={item.id}
                className="flex items-center justify-between p-3 bg-stone-50/50 dark:bg-stone-900/50 rounded-xl border border-stone-100/50 dark:border-stone-800/50 transition-all group"
              >
                <div
                  className="flex items-center gap-3 flex-1 cursor-pointer"
                  onClick={() => toggleItem(item.id, false)}
                >
                  <div className="w-5 h-5 rounded-full bg-soft-sage flex items-center justify-center flex-shrink-0 text-white">
                    <Check className="w-3 h-3" />
                  </div>
                  <span className="text-stone-400 line-through decoration-stone-400">
                    {item.name}
                  </span>
                </div>
                <button
                  onClick={() => deleteItem(item.id)}
                  className="text-stone-300 hover:text-red-400 p-2 transition-colors opacity-0 group-hover:opacity-100 focus:opacity-100"
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

"use client";

import { useState } from "react";
import {
  Plus,
  Search,
  Edit2,
  Trash2,
  CalendarPlus,
  Loader2,
} from "lucide-react";

// import { AppShell } from "@/components/layout/AppShell"; // Removed

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent } from "@/components/ui/card";
import { MealEditor } from "@/components/meals/MealEditor";
import { useMeals } from "@/lib/hooks/useMeals";
import { usePlannerActions } from "@/lib/hooks/usePlannerActions";
import { Meal, MealType } from "@/types";
import { cn } from "@/lib/utils/cn";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { format } from "date-fns";
import { useRouter } from "next/navigation";
import { useRequireAuth, AuthLoadingScreen } from "@/lib/hooks/useRequireAuth";
import { FloatingActionButton } from "@/components/ui/floating-action-button";

export default function MealsPage() {
  const { user: authUser, loading: authLoading } = useRequireAuth();
  const { meals, loading, addMeal, updateMeal, deleteMeal } = useMeals();
  const { addMealToDate } = usePlannerActions();
  const router = useRouter();

  const [searchTerm, setSearchTerm] = useState("");
  const [isEditorOpen, setEditorOpen] = useState(false);
  const [editingMeal, setEditingMeal] = useState<Meal | null>(null);

  // Planning State
  const [planningMeal, setPlanningMeal] = useState<Meal | null>(null);
  const [isPlanDialogOpen, setPlanDialogOpen] = useState(false);
  const [planDate, setPlanDate] = useState(format(new Date(), "yyyy-MM-dd"));
  const [planType, setPlanType] = useState<MealType>("dinner");

  // Auth check after all hooks
  if (authLoading || !authUser) {
    return <AuthLoadingScreen />;
  }

  const filteredMeals = meals.filter((meal) =>
    meal.name.toLowerCase().includes(searchTerm.toLowerCase()),
  );

  const handleEdit = (meal: Meal) => {
    setEditingMeal(meal);
    setEditorOpen(true);
  };

  const handleCreate = () => {
    setEditingMeal(null);
    setEditorOpen(true);
  };

  const handleSave = async (data: Partial<Meal>) => {
    if (editingMeal) {
      await updateMeal(editingMeal.id, data);
    } else {
      await addMeal({
        name: data.name!,
        notes: data.notes,
        tags: data.tags || [],
        prepTime: data.prepTime,
        imageURL: data.imageURL,
      });
    }
  };

  const handleDelete = async (id: string) => {
    if (confirm("Are you sure you want to delete this meal?")) {
      await deleteMeal(id);
    }
  };

  const openPlanDialog = (meal: Meal) => {
    setPlanningMeal(meal);
    setPlanDialogOpen(true);
  };

  const handleAddToPlan = async () => {
    if (!planningMeal) return;
    await addMealToDate(planDate, planType, {
      mealId: planningMeal.id,
      name: planningMeal.name,
      notes: planningMeal.notes,
      mealType: planType,
    });
    setPlanDialogOpen(false);
    alert("Meal added to plan!");
  };

  return (
    <div className="mx-auto max-w-5xl pb-24">
      <header className="mb-8 flex flex-col gap-4 sm:flex-row sm:items-center sm:justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-text-dark dark:text-foreground">
            Meals Library
          </h1>
          <p className="text-stone-500 dark:text-stone-400">
            Your personal collection of recipes.
          </p>
        </div>
        <Button
          onClick={handleCreate}
          className="bg-soft-sage hover:bg-soft-sage/90 text-white shadow-sm"
        >
          <Plus className="mr-2 h-4 w-4" />
          New Meal
        </Button>
      </header>

      <div className="mb-6 relative">
        <Search className="absolute left-3 top-3 h-4 w-4 text-stone-400" />
        <Input
          placeholder="Search meals..."
          className="pl-9 bg-white dark:bg-card border-stone-200 dark:border-stone-800"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {loading ? (
        <div className="flex justify-center py-12">
          <Loader2 className="w-8 h-8 animate-spin text-soft-sage" />
        </div>
      ) : filteredMeals.length === 0 ? (
        <div className="text-center py-20 border border-dashed border-stone-200 dark:border-stone-800 rounded-2xl bg-stone-50/50 dark:bg-stone-900/20">
          <p className="text-stone-500 mb-4">No meals found.</p>
          <Button variant="outline" onClick={handleCreate}>
            Create one
          </Button>
        </div>
      ) : (
        <div className="grid gap-4 sm:grid-cols-2 md:grid-cols-3">
          {filteredMeals.map((meal) => (
            <Card
              key={meal.id}
              className="group relative hover:shadow-lg transition-all border-stone-100 dark:border-stone-800 bg-white dark:bg-card overflow-hidden"
            >
              <CardContent className="p-4">
                <div className="flex items-start justify-between mb-2">
                  <h3 className="font-bold text-lg text-text-dark dark:text-foreground line-clamp-1 pr-8">
                    {meal.name}
                  </h3>
                </div>

                {meal.prepTime && (
                  <p className="text-xs text-stone-500 flex items-center gap-1 mb-2">
                    <span className="w-4 h-4 rounded-full bg-blue-50 dark:bg-blue-900/20 text-blue-600 flex items-center justify-center text-[10px]">
