"use client";

import { Suspense, useState } from "react";
import { format, parseISO, isValid, addDays, subDays } from "date-fns";
import { useSearchParams, useRouter } from "next/navigation";
import {
  ChevronLeft,
  ChevronRight,
  Plus,
  CheckCircle2,
  Circle,
  Loader2,
} from "lucide-react";
import { FloatingActionButton } from "@/components/ui/floating-action-button";

// ... (existing code)

// import { AppShell } from "@/components/layout/AppShell"; // Removed

import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogTrigger,
  DialogHeader,
  DialogTitle,
  DialogDescription,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { usePlan } from "@/lib/hooks/usePlan";
import { useAuth } from "@/components/providers/AuthProvider";
import { useRequireAuth, AuthLoadingScreen } from "@/lib/hooks/useRequireAuth";
import { cn } from "@/lib/utils/cn";
import { MealType, MealEntry, Meal } from "@/types";
import { useMeals } from "@/lib/hooks/useMeals";

import { nutritionService } from "@/lib/services/nutrition";

// MealType definition to ensure type safety in keys
const MEAL_TYPES: MealType[] = ["breakfast", "lunch", "snack", "dinner"];

function TodayContent() {
  const { user: authUser, loading: authLoading } = useRequireAuth();
  const searchParams = useSearchParams();
  const router = useRouter();
  const dateParam = searchParams.get("date");

  // Validate date param or fallback to today
  let currentDate = new Date();
  if (dateParam) {
    const parsed = parseISO(dateParam);
    if (isValid(parsed)) {
      currentDate = parsed;
    }
  }

  const dateKey = format(currentDate, "yyyy-MM-dd");
  const {
    dayPlan,
    loading,
    addMealToDay,
    removeMealFromDay,
    toggleMealCompletion,
  } = usePlan(dateKey);
  const { user } = useAuth();

  const displayDate = format(currentDate, "EEEE, MMMM d");

  const changeDate = (days: number) => {
    const newDate = addDays(currentDate, days);
    router.push(`/today?date=${format(newDate, "yyyy-MM-dd")}`);
  };

  const [addDialogOpen, setAddDialogOpen] = useState(false);
  const [activeMealType, setActiveMealType] = useState<MealType | null>(null);
  const [quickAddName, setQuickAddName] = useState("");
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { meals: familyMeals, addMeal } = useMeals();

  // Now do conditional return AFTER all hooks
  if (authLoading || !authUser) {
    return <AuthLoadingScreen />;
  }

  const filteredSuggestions = quickAddName
    ? familyMeals.filter((m) =>
        m.name.toLowerCase().includes(quickAddName.toLowerCase()),
      )
    : [];

  const handleQuickAdd = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!activeMealType || !quickAddName.trim() || isSubmitting) return;

    setIsSubmitting(true);
    try {
      const existingMeal = familyMeals.find(
        (m) => m.name.toLowerCase() === quickAddName.trim().toLowerCase(),
      );

      let finalMealId = existingMeal?.id;
      let nutritionToAdd = existingMeal?.nutrition;
      let prepTimeToAdd = existingMeal?.prepTime || 0;
      let tagsToAdd = existingMeal?.tags || [];
      let notesToAdd = existingMeal?.notes || "";
      let ingredientsToAdd = existingMeal?.ingredients || [];

      if (!finalMealId) {
        try {
          const enrichedData = await nutritionService.searchFood(
            quickAddName.trim(),
          );
          if (enrichedData) {
            nutritionToAdd = enrichedData.nutrition;
            prepTimeToAdd = enrichedData.prepTime;
            tagsToAdd = enrichedData.tags;
            ingredientsToAdd = enrichedData.ingredients;
            if (enrichedData.description) notesToAdd = enrichedData.description;
          }
        } catch (err) {
          console.warn("Nutrition fetch failed", err);
        }

        try {
          const newId = await addMeal({
            name: quickAddName.trim(),
            notes: notesToAdd,
            tags: tagsToAdd,
            prepTime: prepTimeToAdd,
            nutrition: nutritionToAdd,
            ingredients: ingredientsToAdd,
          });
          finalMealId = newId;
        } catch (err) {
          console.error("Failed to create new meal", err);
          finalMealId = crypto.randomUUID();
        }
      }

      await addMealToDay(activeMealType, {
        mealId: finalMealId!,
        name: quickAddName.trim(),
        notes: notesToAdd,
        mealType: activeMealType,
        nutrition: nutritionToAdd,
      });

      setQuickAddName("");
      setAddDialogOpen(false);
      setShowSuggestions(false);
    } finally {
      setIsSubmitting(false);
    }
  };

  const selectSuggestion = (meal: Meal) => {
    setQuickAddName(meal.name);
    setShowSuggestions(false);
  };

  return (
    <div className="mx-auto max-w-3xl pb-24">
      <header className="mb-6 flex items-center justify-between">
        <Button variant="ghost" size="icon" onClick={() => changeDate(-1)}>
          <ChevronLeft className="h-5 w-5" />
        </Button>
        <div className="text-center">
          <h1 className="text-2xl font-bold tracking-tight">{displayDate}</h1>
          <p className="text-sm text-muted-foreground">
            {dateKey === format(new Date(), "yyyy-MM-dd") ? "Today" : "Plan"}
          </p>
        </div>
        <Button variant="ghost" size="icon" onClick={() => changeDate(1)}>
          <ChevronRight className="h-5 w-5" />
        </Button>
      </header>

      <div className="space-y-6">
        {MEAL_TYPES.map((type) => {
          const meals = dayPlan?.meals?.[type] || [];
          return (
            <section key={type} className="space-y-3">
              <div className="flex items-center justify-between">
                <h2 className="text-lg font-semibold capitalize text-primary">
                  {type}
                </h2>
                <Dialog
                  open={addDialogOpen && activeMealType === type}
                  onOpenChange={(open) => {
                    setAddDialogOpen(open);
                    if (open) setActiveMealType(type);
                  }}
                >
                  <DialogTrigger asChild>
                    <Button variant="ghost" size="sm" className="h-8">
                      <Plus className="mr-1 h-3 w-3" /> Add
                    </Button>
                  </DialogTrigger>
                  <DialogContent>
                    <DialogHeader>
                      <DialogTitle>Add to {type}</DialogTitle>
                      <DialogDescription>
                        Quickly add a meal to your {type} plan.
                      </DialogDescription>
                    </DialogHeader>
                    <form
                      onSubmit={handleQuickAdd}
                      className="space-y-4 pt-4 relative"
                    >
                      <div className="relative">
                        <Input
                          placeholder="Search or enter new meal..."
                          value={quickAddName}
                          onChange={(e) => {
                            setQuickAddName(e.target.value);
                            setShowSuggestions(true);
                          }}
                          onFocus={() => setShowSuggestions(true)}
                          autoFocus
                        />
                        {showSuggestions &&
                          quickAddName &&
                          filteredSuggestions.length > 0 && (
                            <div className="absolute z-10 mt-1 w-full rounded-md border bg-popover shadow-md max-h-[200px] overflow-auto">
                              {filteredSuggestions.map((meal) => (
                                <div
                                  key={meal.id}
                                  className="cursor-pointer px-4 py-2 hover:bg-accent hover:text-accent-foreground text-sm"
                                  onClick={() => selectSuggestion(meal)}
                                >
                                  {meal.name}
                                </div>
                              ))}
                            </div>
                          )}
                      </div>
                      <Button
                        type="submit"
                        className="w-full"
                        disabled={isSubmitting}
                      >
                        {isSubmitting ? (
                          <>
                            <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                            Adding...
                          </>
                        ) : filteredSuggestions.some(
                            (m) =>
                              m.name.toLowerCase() ===
                              quickAddName.trim().toLowerCase(),
                          ) ? (
                          "Add Existing Meal"
                        ) : (
                          "Create & Add Meal"
                        )}
                      </Button>
                    </form>
                  </DialogContent>
                </Dialog>
              </div>

              {loading ? (
                <div className="h-20 rounded-xl border border-dashed bg-muted/20 animate-pulse" />
              ) : meals.length === 0 ? (
                <div className="rounded-xl border border-dashed p-4 text-center text-sm text-muted-foreground bg-muted/5">
                  No meals planned
                </div>
              ) : (
                <div className="grid gap-3">
                  {meals.map((meal) => (
                    <Card
                      key={meal.id}
                      className={cn(
                        "transition-all group",
                        meal.completed && "opacity-60 bg-muted/50",
                      )}
                    >
                      <CardContent className="p-4 flex items-center gap-3">
                        <button
                          onClick={() => toggleMealCompletion(type, meal)}
                          className="text-muted-foreground hover:text-primary transition-colors"
                        >
                          {meal.completed ? (
                            <CheckCircle2 className="h-5 w-5 text-primary" />
                          ) : (
                            <Circle className="h-5 w-5" />
                          )}
                        </button>
                        <div className="flex-1">
                          <p
                            className={cn(
                              "font-medium",
                              meal.completed && "line-through",
                            )}
                          >
                            {meal.name}
                          </p>
                          {meal.notes && (
                            <p className="text-xs text-muted-foreground line-clamp-1">
                              {meal.notes}
                            </p>
                          )}
                        </div>
                        <Button
                          variant="ghost"
                          size="sm"
                          className="h-8 w-8 text-destructive opacity-100 md:opacity-0 md:group-hover:opacity-100"
                          onClick={() => removeMealFromDay(type, meal)}
                        >
                          &times;
                        </Button>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}
            </section>
          );
        })}
      </div>

      <FloatingActionButton
        icon={<Plus className="h-6 w-6" />}
        onClick={() => {
          const hour = new Date().getHours();
          let type: MealType = "snack";
          if (hour < 11) type = "breakfast";
          else if (hour < 15) type = "lunch";
          else if (hour < 21) type = "dinner";

          setActiveMealType(type);
          setAddDialogOpen(true);
        }}
        label="Quick Add Meal"
      />
    </div>
  );
}

export default function TodayPage() {
  return (
    <Suspense
      fallback={
        <div className="flex items-center justify-center p-12">
          <Loader2 className="w-8 h-8 animate-spin text-soft-sage" />
        </div>
      }
    >
      <TodayContent />
    </Suspense>
  );
}

"use client";

import { useState, useEffect } from "react";
import { format, startOfWeek, addDays, isSameDay } from "date-fns";
// import { AppShell } from "@/components/layout/AppShell"; // Removed

import { useAuth } from "@/components/providers/AuthProvider";
import { useRequireAuth, AuthLoadingScreen } from "@/lib/hooks/useRequireAuth";
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "@/lib/firebase/config";
import { DayPlan } from "@/types";
import { cn } from "@/lib/utils/cn";
import { useRouter } from "next/navigation";
import { Loader2 } from "lucide-react";

export default function WeekPage() {
  const {
    user: authUser,
    loading: authLoading,
    activeFamilyId,
  } = useRequireAuth();
  const { user } = useAuth();
  const router = useRouter();
  const [weekPlans, setWeekPlans] = useState<Record<string, DayPlan>>({});
  const [loading, setLoading] = useState(true);

  const today = new Date();
  const startDate = startOfWeek(today, { weekStartsOn: 0 });
  const weekDays = Array.from({ length: 7 }).map((_, i) =>
    addDays(startDate, i),
  );

  useEffect(() => {
    if (!user || !activeFamilyId) {
      setLoading(false);
      return;
    }

    const fetchWeek = async () => {
      setLoading(true);
      try {
        const startStr = format(weekDays[0], "yyyy-MM-dd");
        const endStr = format(weekDays[6], "yyyy-MM-dd");

        const q = query(
          collection(db, "families", activeFamilyId, "dayPlans"),
          where("date", ">=", startStr),
          where("date", "<=", endStr),
        );

        const snapshot = await getDocs(q);
        const plans: Record<string, DayPlan> = {};
        snapshot.forEach((doc) => {
          const data = doc.data();
          const mealsMap = data.meals || {};
          const id = doc.id;

          plans[id] = {
            id: data.id || id,
            date: data.date || id,
            familyId: activeFamilyId,
            updatedAt: data.updatedAt || 0,
            mealCount: data.mealCount || 0,
            meals: {
              breakfast: mealsMap.breakfast || data["meals.breakfast"] || [],
              lunch: mealsMap.lunch || data["meals.lunch"] || [],
              snack: mealsMap.snack || data["meals.snack"] || [],
              dinner: mealsMap.dinner || data["meals.dinner"] || [],
            },
          };
        });
        setWeekPlans(plans);
      } catch (error) {
        console.error("Error fetching week:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchWeek();
  }, [user, activeFamilyId]);

  if (authLoading || !authUser) {
    return <AuthLoadingScreen />;
  }

  const navigateToDay = (dateStr: string) => {
    if (dateStr === format(today, "yyyy-MM-dd")) {
      router.push("/today");
    } else {
      router.push(`/today?date=${dateStr}`);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center p-12">
        <Loader2 className="w-8 h-8 animate-spin text-soft-sage" />
      </div>
    );
  }

  return (
    <div className="mx-auto max-w-7xl pb-12">
      <header className="mb-8 flex flex-col md:flex-row md:items-end justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold tracking-tight text-text-dark dark:text-foreground">
            This Week
          </h1>
          <p className="text-stone-500 dark:text-stone-400">
            {format(weekDays[0], "MMMM d")} -{" "}
            {format(weekDays[6], "MMMM d, yyyy")}
          </p>
        </div>
      </header>

      <div className="grid gap-3 md:gap-4 md:grid-cols-7 sm:grid-cols-2 grid-cols-1 pb-24 md:pb-0">
        {weekDays.map((day) => {
          const dateStr = format(day, "yyyy-MM-dd");
          const plan = weekPlans[dateStr];
          const isToday = isSameDay(day, today);

          const breakfastCount = plan?.meals?.breakfast?.length || 0;
          const lunchCount = plan?.meals?.lunch?.length || 0;
          const dinnerCount = plan?.meals?.dinner?.length || 0;
          const snackCount = plan?.meals?.snack?.length || 0;
          const total = breakfastCount + lunchCount + dinnerCount + snackCount;

          return (
            <div
              key={dateStr}
              onClick={() => navigateToDay(dateStr)}
              className={cn(
                "flex md:flex-col items-center md:items-start p-3 md:p-4 rounded-xl md:rounded-2xl border transition-all hover:shadow-lg cursor-pointer min-h-[80px] md:h-full md:min-h-[200px] gap-3 md:gap-0",
                isToday
                  ? "bg-soft-sage/10 border-soft-sage/50 ring-1 ring-soft-sage/50"
                  : "bg-white dark:bg-card border-stone-100 dark:border-stone-800 hover:border-soft-sage/30",
              )}
            >
              {/* Date Header: Row on Mobile, Column on Desktop */}
              <div className="flex md:flex-col items-center md:items-start gap-3 md:gap-0 md:mb-4 w-16 md:w-full flex-shrink-0">
                <span
                  className={cn(
                    "text-[10px] md:text-xs font-bold uppercase tracking-wider block md:mb-1 order-2 md:order-1",
                    isToday ? "text-soft-sage" : "text-stone-400",
                  )}
                >
                  {format(day, "EEE")}
                </span>
                <div
                  className={cn(
                    "text-lg md:text-2xl font-bold md:mx-auto md:w-10 md:h-10 flex items-center justify-center rounded-full order-1 md:order-2",
                    isToday
                      ? "md:bg-soft-sage md:text-white md:shadow-md text-soft-sage"
                      : "text-text-dark dark:text-foreground",
                  )}
                >
                  {format(day, "d")}
                </div>
              </div>

              {/* Vertical Divider for Mobile */}
              <div className="h-10 w-px bg-stone-100 dark:bg-stone-800 md:hidden" />

              {/* Meals List */}
              <div className="flex-1 md:space-y-2 md:mt-auto text-xs w-full min-w-0">
                {total === 0 ? (
                  <div className="text-stone-300 dark:text-stone-600 md:h-20 flex items-center md:justify-center md:border-2 md:border-dashed md:border-stone-100 md:dark:border-stone-800 md:rounded-xl md:bg-stone-50/50 md:dark:bg-stone-900/20 italic">
                    <span className="md:hidden">No meals planned</span>
                    <span className="hidden md:inline">Empty</span>
                  </div>
                ) : (
                  <div className="flex flex-row md:flex-col gap-2 md:gap-0 overflow-x-auto md:overflow-visible no-scrollbar">
                    {/* Mobile: Just show counts or icons? No, let's show pills but scrollable horizontally if many */}
                    {plan?.meals?.breakfast?.length > 0 && (
                      <div className="space-y-1 min-w-[80px] md:min-w-0">
                        <span className="text-[10px] font-bold text-orange-400 block uppercase tracking-wider">
                          Breakfast
                        </span>
                        {plan.meals.breakfast.map((m) => (
                          <div
                            key={m.id}
                            className="truncate pl-2 border-l-2 border-orange-200 dark:border-orange-900 text-stone-600 dark:text-stone-300 py-0.5"
                          >
                            {m.name}
                          </div>
                        ))}
                      </div>
                    )}
                    {plan?.meals?.lunch?.length > 0 && (
                      <div className="space-y-1 min-w-[80px] md:min-w-0">
