"use client";

import { useAuth } from "@/components/providers/AuthProvider";
import { useRouter, usePathname } from "next/navigation";
import { useEffect, useState } from "react";
import { Loader2 } from "lucide-react";

const PUBLIC_PATHS = ["/", "/offline", "/manifest.json", "/sw.js"];

export function AuthGuard({ children }: { children: React.ReactNode }) {
  const { user, loading } = useAuth();
  const router = useRouter();
  const pathname = usePathname();
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);
  }, []);

  useEffect(() => {
    if (!mounted) return;

    if (!loading && !user && pathname && !PUBLIC_PATHS.includes(pathname)) {
      console.log("AuthGuard: Redirecting to / from", pathname as string);
      router.push("/");
    }
  }, [user, loading, pathname, router, mounted]);

  if (loading || !mounted) {
    return (
      <div className="flex h-screen items-center justify-center bg-background">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  if (!user && pathname && !PUBLIC_PATHS.includes(pathname)) {
    return null;
  }

  return <>{children}</>;
}

