describe("useMeals", () => {
  beforeEach(() => {
    vi.clearAllMocks();
    (useAuthStore as any).mockReturnValue({
      user: { uid: "user1" },
      activeFamilyId: "family1",
    });
    (collection as any).mockReturnValue("mock-collection");
  });

  it("should fetch meals and update state", async () => {
    const mockMeals = [
      { id: "1", name: "Pasta" },
      { id: "2", name: "Salad" },
    ];

    (onSnapshot as any).mockImplementation((query, callback) => {
      callback({
        docs: mockMeals.map((m) => ({
          id: m.id,
          data: () => m,
        })),
      });
      return vi.fn(); // unsubscribe
    });

    const { result } = renderHook(() => useMeals());

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    expect(result.current.meals).toHaveLength(2);
    expect(result.current.meals[0].name).toBe("Pasta");
  });

  it("should add a meal", async () => {
    (addDoc as any).mockResolvedValue({ id: "new-id" });

    const { result } = renderHook(() => useMeals());

    await result.current.addMeal({
      name: "Pizza",
      ingredients: [],
      mealType: "dinner",
    } as any);

    expect(addDoc).toHaveBeenCalledWith(
      "mock-collection",
      expect.objectContaining({
        name: "Pizza",
        familyId: "family1",
        createdBy: "user1",
      }),
    );
  });

  it("should update a meal", async () => {
    (doc as any).mockReturnValue("mock-doc-ref");

    const { result } = renderHook(() => useMeals());

    await result.current.updateMeal("1", { name: "Super Pasta" });

    expect(doc).toHaveBeenCalledWith(
      expect.anything(),
      "families",
      "family1",
      "meals",
      "1",
    );
    expect(updateDoc).toHaveBeenCalledWith("mock-doc-ref", {
      name: "Super Pasta",
    });
  });

  it("should delete a meal", async () => {
    (doc as any).mockReturnValue("mock-doc-ref");

    const { result } = renderHook(() => useMeals());

    await result.current.deleteMeal("1");

    expect(deleteDoc).toHaveBeenCalledWith("mock-doc-ref");
  });
});

